import "./App.css";
import React from "react";
import { CardList } from "./component/CardList/card-list.component.jsx";
class App extends React.Component {
  constructor() {
    super();

    this.state = {
      monsters: [],
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) =>
        this.setState({
          monsters: users,
        })
      );
  }

  render() {
    return (
      <div className="App">

        <input type="search" placeholder="search monster" onChange = {(e) => e.target.value}/>
        <CardList monsters ={this.state.monsters}/>
      </div>
    );
  }
}

export default App;
